// Object "Get event details" functions:
function checkconfigurationforevent() { // Function to check if CLI and Object remediation is enabled and if a Payload is send via the Webhook.
    if (payload !== null) // Don't try to get the Events if the payload is not defined.
    {
        return 1;
    }
    else {
        return 0;
    }
}

function createresourcelist() { // Function to build the Resource List that will be used for Auto Remediation.
    let p = configuration.eventuuid;
    let laceworkAPIResult = getAction(p);
    link = payload.event_link;
    if (laceworkAPIResult.result.result.data[0].ENTITY_MAP.ViolationReason[0].REC_ID == configuration.rec_id) {
        event_aws_accountid = laceworkAPIResult.result.result.data[0].ENTITY_MAP.RecId[0].ACCOUNT_ID;
        event_aws_account_key = event_aws_accountid;
        event_aws_account_key += "_aws_access_key_id";
        event_aws_secret_key = event_aws_accountid;
        event_aws_secret_key += "_aws_secret_access_key";
        let allresourcesArr = laceworkAPIResult.result.result.data[0].ENTITY_MAP.Resource;
        for (let i = 0; i < allresourcesArr.length; i++) {
            if (!configuration.IgnoreList.includes(allresourcesArr[i].VALUE)) { // The root IAM of the Event will be checked against the configuration list of to ignore IAM accounts.
                ressourcesArr.push(allresourcesArr[i].VALUE); // If the iam account is not on the list it will be pushed into the array.
            }
            else {
                ressourcesIgnoredArr.push(allresourcesArr[i].VALUE); // If the iam account is on the list and violating the policy it will be pushed into the second list.
            }
        }
    }
}

function geteventid() { // Function to get the Event ID from the Webhook Payload.
    return `${payload.event_id}`;
}

// Object "Get report details" functions:
function checkconfigurationforreport() { // Function to check if CLI and Object Auto Remediation is enabled. Also check if it is not triggered via a Webhook with a payload send.
    if (configuration.dotheremediationviacli == "true" && configuration.dotheremediationviaobject == "true") {
        return 0;
    }
    else if (payload == null) // Only trigger the Report collection if there is no payload defined.
    {
        return 1;
    }
    else {
        return 0;
    }
}

function createResourceListfromReport() { // Function to build the Resource List that will be used to Remediate.
    let p = configuration.reportuuid;
    let laceworkAPIResult = getAction(p);
    let findreason = 0;
    let recommendationsArr = laceworkAPIResult.result.result.data[0].recommendations;
    for (let i = 0; i < recommendationsArr.length; i++) {
        if (recommendationsArr[i].REC_ID == configuration.rec_id) {
            link = recommendationsArr[i].INFO_LINK;
            findreason = i;
        }
        else {
            // Didn't found the right reason
        }
    }
    let allresourcesArr = laceworkAPIResult.result.result.data[0].recommendations[findreason].VIOLATIONS;
    event_aws_accountid = configuration.awsaccountid;
    event_aws_account_key = event_aws_accountid;
    event_aws_account_key += "_aws_access_key_id";
    event_aws_secret_key = event_aws_accountid;
    event_aws_secret_key += "_aws_secret_access_key";
    for (let i = 0; i < allresourcesArr.length; i++) {
        if (!configuration.IgnoreList.includes(allresourcesArr[i].resource)) { // Check against the list of to ignore IAM accounts.
            ressourcesArr.push(allresourcesArr[i].resource); // If the IAM account is not on the list it will be pushed into the array.
        }
        else {
            ressourcesIgnoredArr.push(allresourcesArr[i].resource); // If the IAM account is on the list and violating the policy it will be pushed into the second ressource list.
        }
    }
}

