// Object "Remediated" functions:
function checkforslack() { //Check if you want to also see the resources via the slack messages.
    if (indexSlack < ressourcesArr.length && configuration.sendslackmessagesforremediation == "true") {
        return 1;
    }
    else {
        return 0;
    }
}


function sendslackmessage() { // Function to send out a slack message for every IAM account that will be remediated.
    let resource = ressourcesArr[indexSlack];
    let arr = resource.split("::");
    let arr2 = arr[arr.length - 1].split(":");
    if (configuration.dotheremediationviacli == "true") {
        return `Compliance Violation ${configuration.rec_id} with description ${configuration.violationdescription}. The following resource will be auto remediated: ${arr2[arr2.length - 1]}. Using AWS CLI command to do the remediation of the resource. More information about the event is available at ${link}`;
    }
    else {
        return `Compliance Violation ${configuration.rec_id} with description ${configuration.violationdescription}. Auto Remediation is disabled! The following resource would be auto remediated: ${arr2[arr2.length - 1]}. More information about the event is available at ${link}`;
    }
}

// Object "Ignored" functions:
function checkforignoredslack() { //Check if you want to also see the ignored resources via the slack messages.
    if (indexIgnored < ressourcesIgnoredArr.length && configuration.sendslackmessagesforignored == "true") {
        return 1;
    }
    else {
        return 0;
    }
}

function sendslackmessageforignored() { // Function to send out a slack message for every resource that will be ignored but is violating the policy.
    let resource = ressourcesIgnoredArr[indexIgnored];
    let arr = resource.split("::");
    let arr2 = arr[arr.length - 1].split(":");
    return `Compliance Violation ${configuration.rec_id} with description ${configuration.violationdescription}. The following resource is NOT auto remediated as it is ignored by this map via the configuration: ${arr2[arr2.length - 1]}. More information about the event is available at ${link}`;
}

