function createcurrentsuppressionsetting() { //Function to put the current settings of a suppression configuration.
    let q = configuration.suppressionuuid;
    let laceworkAPIResult = getAction(q);
    let suppressionConditions = `laceworkAPIResult.result.result.data[0].${configuration.rec_id}.suppressionConditions`;
    suppressionTag = "NotConfigured";
    for (let i = 0; i < suppressionConditions.length; i++) {
        let suppressionResourceTags = laceworkAPIResult.result.result.data[0].LW_S3_2.suppressionConditions[i].resourceTags;
        for (let j = 0; j < suppressionResourceTags.length; j++) {
            if (suppressionResourceTags[j].key == configuration.tagname)
            {
                suppressionTag = "Configured";
                suppressionValue = suppressionResourceTags[j].value;
            }
        }
    }
}

