var bucketArr = []; // Array of S3 Buckets that will be filled with the resource information from the event.
var indexRemediate = 0; // Index for the S3 Bucket Remediate of the Map
var indexSlack = 0; // Index of the Slack Loop to send out information about the S3 Bucket remediation.
var bucketIgnoredArr = []; // Array of S3 Buckets that are violating but will be ignored.
var indexIgnored = 0; //Index of the S3 Buckets that are violating but will be ignored.

let payload = trigger.payload; // The Payload that was send by the Webhook Trigger. Information about the Payload is available at https://support.lacework.com/hc/en-us/articles/360034367393-Webhook

function checkconfiguration()  { // Function to check if two out of four Auto Remediation steps are enabled.
    if (configuration.dothedeletebucketpolicyremediationviacli == "true" && configuration.dothereplacebucketpolicyremediationviacli == "true") {
        return 0;
    }
    else if (configuration.dothedeletebucketpolicyremediationviacli == "true" && configuration.dothedeletebucketpolicyremediationviaobject == "true") {
        return 0;
    }
    else if (configuration.dothedeletebucketpolicyremediationviacli == "true" && configuration.dothereplacebucketpolicyremediationviaobject == "true") {
        return 0;
    }
    else if (configuration.dothereplacebucketpolicyremediationviacli == "true" && configuration.dothedeletebucketpolicyremediationviaobject == "true") {
        return 0;
    }
    else if (configuration.dothereplacebucketpolicyremediationviacli == "true" && configuration.dothereplacebucketpolicyremediationviaobject == "true") {
        return 0;
    }
    else if (configuration.dothereplacebucketpolicyremediationviaobject == "true" && configuration.dothedeletebucketpolicyremediationviaobject == "true") {
        return 0;
    }
    else {
        return 1;
    }
}

function geteventid() { // Function to get the Event ID from the Webhook Payload.
    return `${payload.event_id}`;
}

function createBucketList() { // Function to build the S3 Bucket List that will be used to Remediate.
    let p = configuration.eventuuid;
    let laceworkAPIResult = getAction(p);
    let resourcesArr = laceworkAPIResult.result.result.data[0].ENTITY_MAP.Resource;
    for (let i = 0; i < resourcesArr.length; i++) {
        if (!configuration.bucketIgnoreList.includes(resourcesArr[i].VALUE)) { // The S3 Bucket of the Event will be checked against the configuration list of to ignore S3 buckets.
            bucketArr.push (resourcesArr[i].VALUE); // If the bucket is not on the list it will be pushed into the array.
        }
        else {
            bucketIgnoredArr.push (resourcesArr[i].VALUE); // If the bucket is on the list and violating the policy it will be pushed into the second bucket list.
        }
    }
}

function printBucketName() { // Function to print the S3 buckets that will be remediated.
    let bucket = bucketArr[indexRemediate]
    let arr = bucket.split(":::")
    return `echo Compliance Violation ${configuration.policyID} for the S3 bucket ${arr[arr.length-1]}`;
}

function createbackup() { // Function to backup the S3 bucket policy before it is replaced.
    if (configuration.createbackup == "true") {
        let bucket = bucketArr[indexRemediate]
        let arr = bucket.split(":::")
        let currentdate = Date.now();
        return `aws s3api get-bucket-policy --bucket ${arr[arr.length-1]} > ${configuration.backupfolder}${arr[arr.length-1]}-${currentdate}.json`; 
    }
    else {
        return `Creating backup inside the configuration is disabled. Skipping.`
    }
}

function deleteviaawscli() { // Delete the Bucket Policy via CLI
    let bucket = bucketArr[indexRemediate];
    let arr = bucket.split(":::");
    return `aws s3api delete-bucket-policy --bucket ${arr[arr.length-1]}`; // Using the delete-bucket-policy method. Feel free to replace it with whatever you think is the right aws cli command to trigger.
}

function createnewpolicyfile() {
    let bucket = bucketArr[indexRemediate];
    let arr = bucket.split(":::");
    configuration.bucketpolicy.Statement[0].Resource = bucket;
    let myoutput = configuration.bucketpolicy.replace(/"/g, '\\"');
    return `echo "${myoutput}" > ${configuration.inputfolder}${arr[arr.length-1]}.json`
