function remediateviaobject() { // Remediate the S3 buckets back to private acl.
    let bucket = bucketArr[indexRemediate];
    let arr = bucket.split(":::");
    return `${arr[arr.length-1]}`; //Using the canned acl private.
}

function sendslackmessage() { // Function to send out a slack message for every S3 bucket that will be remediated.
    let bucket = bucketArr[indexSlack];
    let arr = bucket.split(":::");
    let event_link = payload.event_link;
    if (configuration.dothedeletebucketpolicyremediationviacli == "true") {
        return `Compliance Violation ${configuration.policyID} The following S3 bucket is auto remediated back to ${configuration.violationdescription}: ${arr[arr.length-1]}. Executing CLI command aws s3api delete-bucket-policy --bucket ${arr[arr.length-1]}. More information about the event is available at ${event_link}`;
    }
    else if (configuration.dothereplacebucketpolicyremediationviacli == "true") {
        return `Compliance Violation ${configuration.policyID} The following S3 bucket is auto remediated back to ${configuration.violationdescription}: ${arr[arr.length-1]}. Executing CLI command aws s3api put-bucket-policy --bucket ${arr[arr.length-1]} --policy file://policy.json. More information about the event is available at ${event_link}`;
    }
    else if (configuration.dothedeletebucketpolicyremediationviaobject == "true") {
        return `Compliance Violation ${configuration.policyID} The following S3 bucket is auto remediated back to ${configuration.violationdescription}: ${arr[arr.length-1]}. Using the Kaholo S3 object to do the delete of the bucket policy remediation. More information about the event is available at ${event_link}`;
    }
    else if (configuration.dothereplacebucketpolicyremediationviaobject == "true") {
        return `Compliance Violation ${configuration.policyID} The following S3 bucket is auto remediated back to ${configuration.violationdescription}: ${arr[arr.length-1]}. Using the Kaholo S3 object to do the replace of the bucket policy remediation. More information about the event is available at ${event_link}`;
    }
    else {
        return `Compliance Violation ${configuration.policyID}. Auto Remediation is diabled! The following S3 bucket would be auto remediated ${configuration.violationdescription}: ${arr[arr.length-1]}. More information about the event is available at ${event_link}`;   
    }
}

function sendslackmessageforignored() { // Function to send out a slack message for every S3 bucket that will be ignored but is violating the policy.
    let bucket = bucketIgnoredArr[indexIgnored];
    let arr = bucket.split(":::");
    let event_link = payload.event_link;
    return `Compliance Violation ${configuration.policyID} The following S3 bucket is NOT remediated back to ${configuration.violationdescription}: ${arr[arr.length-1]}. It is ignored by the configuration of the Map. We recommend using tags in AWS to suppress this S3 bucket from triggering alerts in Lacework. More information about the event is available at ${event_link}`;    
}

