var bucketArr = []; // Array of S3 Buckets that will be filled with the resource information from the event.
var indexRemediate = 0; // Index for the S3 Bucket Remediate of the Map
var indexSlack = 0; // Index of the Slack Loop to send out information about the S3 Bucket remediation.
var bucketIgnoredArr = []; // Array of S3 Buckets that are violating but will be ignored.
var indexIgnored = 0; //Index of the S3 Buckets that are violating but will be ignored.
var indexFileCreation = 0; //Index for creation of the JSON files.
var indexRemediate2 = 0; //Index for a second CLI command used to replace the policy

let payload = trigger.payload; // The Payload that was send by the Webhook Trigger. Information about the Payload is available at https://support.lacework.com/hc/en-us/articles/360034367393-Webhook

function checkconfiguration()  { // Function to check if both Auto Remediation steps are enabled.
    if (configuration.enablebucketloggingviacli == "true" && configuration.enablebucketloggingviaobject == "true") {
        return 0;
    }
    else {
        return 1;
    }
}

function geteventid() { // Function to get the Event ID from the Webhook Payload.
    return `${payload.event_id}`;
}

function createBucketList() { // Function to build the S3 Bucket List that will be used to Remediate.
    let p = configuration.eventuuid;
    let laceworkAPIResult = getAction(p);
    let resourcesArr = laceworkAPIResult.result.result.data[0].ENTITY_MAP.Resource;
    for (let i = 0; i < resourcesArr.length; i++) {
        if (!configuration.bucketIgnoreList.includes(resourcesArr[i].VALUE)) { // The S3 Bucket of the Event will be checked against the configuration list of to ignore S3 buckets.
            bucketArr.push (resourcesArr[i].VALUE); // If the bucket is not on the list it will be pushed into the array.
        }
        else {
            bucketIgnoredArr.push (resourcesArr[i].VALUE); // If the bucket is on the list and violating the policy it will be pushed into the second bucket list.
        }
    }
}

function printBucketName() { // Function to print the S3 buckets that will be remediated.
    let bucket = bucketArr[indexRemediate]
    let arr = bucket.split(":::")
    return `echo Compliance Violation ${configuration.policyID} for the S3 bucket ${arr[arr.length-1]}`;
}

function printTargetBucketName() { // Function to print the S3 buckets that will be used as Target Bucket for logging.
    return `echo Target Bucket for Logging ${configuration.loggingconfiguration.LoggingEnabled.TargetBucket} needs to be prepared.`;
}

function getTargetBucketName() { // Function to print the S3 buckets that will be used as Target Bucket for logging.
    return `${configuration.loggingconfiguration.LoggingEnabled.TargetBucket}`;
}

function putbucketacl() { // Put the right Bucket ACL on the Target S3 Bucket for logging before applying the bucket logging
    return `aws s3api put-bucket-acl --bucket ${configuration.loggingconfiguration.LoggingEnabled.TargetBucket} --grant-write URI=http://acs.amazonaws.com/groups/s3/LogDelivery --grant-read-acp URI=http://acs.amazonaws.com/groups/s3/LogDelivery`; // Using the put-bucket-acl to configure the Target Bucket correctly.
}

function checkconditionfilecreation() { //Check condition of the file creation texteditor object if it can execute.
    if (indexRemediate == bucketArr.length && indexFileCreation < bucketArr.length) return true
    return false
}

function getfilepath() { //Getting the filepath and name for the file that will be created.
    let bucket = bucketArr[indexFileCreation];
    let arr = bucket.split(":::");
    return `${configuration.inputfolder}${arr[arr.length-1]}-${configuration.policyID}.json`;
}

function getfilecontent() { //Getting the file content of what will be written into the file itself.
    let bucket = bucketArr[indexFileCreation];
    let arr = bucket.split(":::");
    const myjson = JSON.stringify(configuration.loggingconfiguration);
    return `${myjson}`;
}

function checkconditionforapplyinglogging() { //Check the condition for replace the current bucket policy via the CLI.
    if (indexRemediate == bucketArr.length && indexFileCreation == bucketArr.length && indexRemediate2 < bucketArr.length) return true
    return false
}

function enablebucketlogging() { // Replace the Bucket Policy via CLI
    let bucket = bucketArr[indexRemediate2];
    let arr = bucket.split(":::");
    let myoutput = JSON.stringify(configuration.bucketpolicy);
    return `aws s3api put-bucket-logging --bucket ${arr[arr.length-1]} --bucket-logging-status file://${configuration.inputfolder}${arr[arr.length-1]}-${configuration.policyID}.json`; //Using the setting --acl private. Feel free to replace it with whatever you think is the right aws cli command to trigger.
}

function getbucketname() { // Function to print the S3 buckets that will be remediated.
    let bucket = bucketArr[indexRemediate]
    let arr = bucket.split(":::")
    return `${arr[arr.length-1]}`;
}

function checkconditionfordeletionviaobject() {
    if (indexRemediate == bucketArr.length && indexRemediate2 < bucketArr.length) return true
    return false
}

function getbucketnameforremediation() {
    let bucket = bucketArr[indexRemediate2]
