function checkconfigurationforreport() { // Function to check if CLI and Object Auto Remediation is enabled. Also check if it is not triggered via a Webhook with a payload send.
    if (configuration.dotheremediationviacli == "true" && configuration.dotheremediationviaobject == "true") {
        return 0;
    }
    else if (payload == null) // Only trigger the Report collection if there is no payload defined.
    {
        return 1;
    }
    else {
        return 0;
    }
}

function createBucketListfromReport() { // Function to build the S3 Bucket List that will be used to Remediate.
    let p = configuration.reportuuid;
    let laceworkAPIResult = getAction(p);
    let findreason = 0;
    let recommendationsArr = laceworkAPIResult.result.result.data[0].recommendations;
    for (let i = 0; i < recommendationsArr.length; i++) {
        if (recommendationsArr[i].REC_ID == configuration.rec_id) {
            link = recommendationsArr[i].INFO_LINK;
            findreason = i;
        }
        else {
            // Didn't found the right reason
        }
    }
    let resourcesArr = laceworkAPIResult.result.result.data[0].recommendations[findreason].VIOLATIONS;
    resourcesArrLength = resourcesArr.length;
    event_aws_accountid = configuration.awsaccountid;
    event_aws_account_key = event_aws_accountid;
    event_aws_account_key += "_aws_access_key_id";
    event_aws_secret_key = event_aws_accountid;
    event_aws_secret_key += "_aws_secret_access_key";
    for (let i = 0; i < resourcesArr.length; i++) {
        if (!configuration.bucketIgnoreList.includes(resourcesArr[i].resource)) { // The S3 Bucket of the Event will be checked against the configuration list of to ignore S3 buckets.
            bucketArr.push(resourcesArr[i].resource); // If the bucket is not on the list it will be pushed into the array.
        }
        else {
            bucketIgnoredArr.push(resourcesArr[i].resource); // If the bucket is on the list and violating the policy it will be pushed into the second bucket list.
        }
    }
    bucketArrLength = bucketArr.length;
    bucketIgnoredArrLength = bucketIgnoredArr.length;
    bucketConfiguredIgnoredArrLength = configuration.bucketIgnoreList.length;
}

