// Object "Get event details" functions:
function checkconfigurationforevent()  { // Function to check if two out of four Auto Remediation steps are enabled.
    if (configuration.dothedeletebucketpolicyremediationviacli == "true" && configuration.dothereplacebucketpolicyremediationviacli == "true") {
        return 0;
    }
    else if (configuration.dothedeletebucketpolicyremediationviacli == "true" && configuration.dothedeletebucketpolicyremediationviaobject == "true") {
        return 0;
    }
    else if (configuration.dothedeletebucketpolicyremediationviacli == "true" && configuration.dothereplacebucketpolicyremediationviaobject == "true") {
        return 0;
    }
    else if (configuration.dothereplacebucketpolicyremediationviacli == "true" && configuration.dothedeletebucketpolicyremediationviaobject == "true") {
        return 0;
    }
    else if (configuration.dothereplacebucketpolicyremediationviacli == "true" && configuration.dothereplacebucketpolicyremediationviaobject == "true") {
        return 0;
    }
    else if (configuration.dothereplacebucketpolicyremediationviaobject == "true" && configuration.dothedeletebucketpolicyremediationviaobject == "true") {
        return 0;
    }
    else if (payload !== null) // Don't try to get the Events if the payload is not defined.
    {
        return 1;
    }
    else {
        return 0;
    }
}

function createBucketList() { // Function to build the S3 Bucket List that will be used for Auto Remediation.
    let p = configuration.eventuuid;
    let laceworkAPIResult = getAction(p);
    link = payload.event_link;
    if (laceworkAPIResult.result.result.data[0].ENTITY_MAP.ViolationReason[0].REC_ID == configuration.policyID) {
        event_aws_accountid = laceworkAPIResult.result.result.data[0].ENTITY_MAP.RecId[0].ACCOUNT_ID;
        event_aws_account_key = event_aws_accountid;
        event_aws_account_key += "_aws_access_key_id";
        event_aws_secret_key = event_aws_accountid;
        event_aws_secret_key += "_aws_secret_access_key";
        if (configuration.autoremediationonlyfornewviolations == "true")
        {
            let resourcesArr = laceworkAPIResult.result.result.data[0].ENTITY_MAP.NewViolation;
            for (let i = 0; i < resourcesArr.length; i++) {
            if (!configuration.bucketIgnoreList.includes(resourcesArr[i].RESOURCE)) { // The S3 Bucket of the Event will be checked against the configuration list of to ignore S3 buckets.
                bucketArr.push(resourcesArr[i].RESOURCE); // If the bucket is not on the list it will be pushed into the array.
            }
            else {
                bucketIgnoredArr.push(resourcesArr[i].RESOURCE); // If the bucket is on the list and violating the policy it will be pushed into the second bucket list.
            }
        }
        }
        else
        {
            let resourcesArr = laceworkAPIResult.result.result.data[0].ENTITY_MAP.Resource;
            for (let i = 0; i < resourcesArr.length; i++) {
            if (!configuration.bucketIgnoreList.includes(resourcesArr[i].VALUE)) { // The S3 Bucket of the Event will be checked against the configuration list of to ignore S3 buckets.
                bucketArr.push(resourcesArr[i].VALUE); // If the bucket is not on the list it will be pushed into the array.
            }
            else {
                bucketIgnoredArr.push(resourcesArr[i].VALUE); // If the bucket is on the list and violating the policy it will be pushed into the second bucket list.
            }
        }
        }
    }
}

