var bucketArr = []; // Array of S3 Buckets that will be filled with the resource information from the event.
var indexPrint = 0; // Index for the Print loop of the Map
var indexRemediate = 0; // Index for the S3 Bucket Remediate of the Map
var indexSlack = 0; // Index of the Slack Loop to send out information about the S3 Bucket remediation.

let payload = trigger.payload; // The Payload that was send by the Webhook Trigger. Information about the Payload is available at https://support.lacework.com/hc/en-us/articles/360034367393-Webhook

function geteventid() { // Function to get the Event ID from the Webhook Payload.
    return `${payload.event_id}`;
}

function createBucketList() { // Function to build the S3 Bucket List that will be used to Remediate.
    let p = configuration.eventuuid;
    let laceworkAPIResult = getAction(p);
    let resourcesArr = laceworkAPIResult.result.result.data[0].ENTITY_MAP.Resource;
    for (let i = 0; i < resourcesArr.length; i++) {
        if (!configuration.bucketIgnoreList.includes(resourcesArr[i].VALUE)) { // The S3 Bucket of the Event will be checked against the configuration list of to ignore S3 buckets.
            bucketArr.push (resourcesArr[i].VALUE); // If the bucket is not on the list it will be pushed into the array.
        }
    }
}

function printBucketName() { // Function to print the S3 buckets that will be remediated.
    let bucket = bucketArr[indexPrint]
    let arr = bucket.split(":::")
    return `echo Compliance Violation LW_S3_21 for the S3 bucket ${arr[arr.length-1]}`;
}

function sendslackmessage() { // Function to send out a slack message for every S3 bucket that will be remediated.
    let bucket = bucketArr[indexSlack];
    let arr = bucket.split(":::");
    let event_link = payload.event_link;
    return `Compliance Violation LW_S3_21 The following S3 bucket is auto remediated back to ensure the attached S3 bucket policy does not grant global Put permission: ${arr[arr.length-1]}. More information about the event is available at ${event_link}`;
}

function checkremediation() //Check if the Remedation should be done?
{
    if (configuration.dotheremediation == "true") {
        if (indexRemediate < bucketArr.length) {
            return 1;
        }
        else {
            return 0;
        }
    }
    else {
        return 0;
    }
}

function remediateBucketName() { // Function to give the remediation step the necessary S3 bucket names.
    let bucket = bucketArr[indexRemediate];
    let arr = bucket.split(":::");
    return `${arr[arr.length-1]}`;
}

function getAction(pId) {  // Help function to get the return value of the event.
   let p = getProcessById(pId)//Get the process. 
   if(!p ||!p.length)return; 
   p = p[p.length-1].actions; //Get all actions 
   let action = p[Object.keys(p)[0]];//get the needed action 
   return action 
}
