var bucketArr = []; // Array of S3 Buckets that will be filled with the resource information from the event.
var indexRemediate = 0; // Index for the S3 Bucket Remediate of the Map
var indexSlack = 0; // Index of the Slack Loop to send out information about the S3 Bucket remediation.
var bucketIgnoredArr = []; // Array of S3 Buckets that are violating but will be ignored.
var indexIgnored = 0; //Index of the S3 Buckets that are violating but will be ignored.

let payload = trigger.payload; // The Payload that was send by the Webhook Trigger. Information about the Payload is available at https://support.lacework.com/hc/en-us/articles/360034367393-Webhook

function checkconfiguration()  { // Function to check if CLI and Object remediation is enabled.
    if (configuration.dotheremediationviacli == "true" && configuration.dotheremediationviaobject == "true") {
        return 0;
    }
    else {
        return 1;
    }
}

function geteventid() { // Function to get the Event ID from the Webhook Payload.
    return `${payload.event_id}`;
}

function createBucketList() { // Function to build the S3 Bucket List that will be used to Remediate.
    let p = configuration.eventuuid;
    let laceworkAPIResult = getAction(p);
    let resourcesArr = laceworkAPIResult.result.result.data[0].ENTITY_MAP.Resource;
    for (let i = 0; i < resourcesArr.length; i++) {
        if (!configuration.bucketIgnoreList.includes(resourcesArr[i].VALUE)) { // The S3 Bucket of the Event will be checked against the configuration list of to ignore S3 buckets.
            bucketArr.push (resourcesArr[i].VALUE); // If the bucket is not on the list it will be pushed into the array.
        }
        else {
            bucketIgnoredArr.push (resourcesArr[i].VALUE); // If the bucket is on the list and violating the policy it will be pushed into the second bucket list.
        }
    }
}

function printBucketName() { // Function to print the S3 buckets that will be remediated.
    let bucket = bucketArr[indexRemediate]
    let arr = bucket.split(":::")
    return `echo Compliance Violation ${configuration.policyID} for the S3 bucket ${arr[arr.length-1]}`;
}

function remediateviaawscli() { // Remediate the S3 buckets back to private acl.
    let bucket = bucketArr[indexRemediate];
    let arr = bucket.split(":::");
    return `aws s3api put-bucket-acl --bucket ${arr[arr.length-1]} --acl private`; //Using the setting --acl private. Feel free to replace it with whatever you think is the right aws cli command to trigger.
}


function remediateviaobject() { // Remediate the S3 buckets back to private acl.
    let bucket = bucketArr[indexRemediate];
    let arr = bucket.split(":::");
    return `${arr[arr.length-1]}`; //Using the canned acl private.
}

function sendslackmessage() { // Function to send out a slack message for every S3 bucket that will be remediated.
    let bucket = bucketArr[indexSlack];
    let arr = bucket.split(":::");
    let event_link = payload.event_link;
    if (configuration.dotheremediationviacli == "true") {
        return `Compliance Violation ${configuration.policyID} The following S3 bucket is auto remediated back to ${configuration.violationdescription}: ${arr[arr.length-1]}. Executing CLI command aws s3api put-bucket-acl --bucket ${arr[arr.length-1]} --acl private. More information about the event is available at ${event_link}`;
    }
    else if (configuration.dotheremediationviaobject == "true") {
        return `Compliance Violation ${configuration.policyID} The following S3 bucket is auto remediated back to ${configuration.violationdescription}: ${arr[arr.length-1]}. Using the Kaholo S3 object to do the remediation. More information about the event is available at ${event_link}`;
    }
    else {
        return `Compliance Violation ${configuration.policyID}. Auto Remdiation is diabled! The following S3 bucket would be auto remediated back to ${configuration.violationdescription}: ${arr[arr.length-1]}. Would execute CLI command aws s3api put-bucket-acl --bucket ${arr[arr.length-1]} --acl private. More information about the event is available at ${event_link}`;   
    }
}

function sendslackmessageforignored() { // Function to send out a slack message for every S3 bucket that will be ignored but is violating the policy.
    let bucket = bucketIgnoredArr[indexIgnored];
    let arr = bucket.split(":::");
    let event_link = payload.event_link;
    return `Compliance Violation ${configuration.policyID} The following S3 bucket is NOT remediated back to ${configuration.violationdescription}: ${arr[arr.length-1]}. It is ignored by the configuration of the Map. We recommend using tags in AWS to suppress this S3 bucket from triggering alerts in Lacework. More information about the event is available at ${event_link}`;    
}

function checkremediationviacli() //Check if the Remedation should be done via the CLI?
{
    if (configuration.dotheremediationviacli == "true") {
        if (indexRemediate < bucketArr.length) {
            return 1;
        }
        else {
            return 0;
        }
    }
    else {
        return 0;
    }
}

function checkremediationviaobject() //Check if the Remedation should be done via the Object?
{
    if (configuration.dotheremediationviaobject == "true") {
        if (indexRemediate < bucketArr.length) {
            return 1;
        }
        else {
            return 0;
        }
    }
    else {
        return 0;
    }
}

function checkforignoredslack() { //Check if you want to also see the ignored buckets via the slack messages.
    if (indexSlack == bucketArr.length && indexIgnored < bucketIgnoredArr.length && configuration.sendslackmessagesforignored == "true") {
        return 1;
    }
    else {
         return 0;
    }
}

function getAction(pId) {  // Help function to get the return value of the event.
   let p = getProcessById(pId)//Get the process. 
   if(!p ||!p.length)return; 
   p = p[p.length-1].actions; //Get all actions 
   let action = p[Object.keys(p)[0]];//get the needed action 
   return action 
}
