// Default variables for this Map
var bucketArr = []; // Array of S3 Buckets that will be used for the Auto Remediation.
var indexRemediate = 0; // Index for the S3 Bucket Auto Remediation Lopps of the Map.
var indexSlack = 0; // Index of the Slack Loop of the map to send out information about the S3 Buckets that will be used for Auto Remediation.
var bucketIgnoredArr = []; // Array of S3 Buckets that are violating but will be ignored based in the bucketIgnoreList list in the configuration.
var indexIgnored = 0; //Index of the S3 Buckets for slack loop that are violating but will be ignored.
var indexTag = 0; // Index for setting tags for the ignored S3 buckets so they will be suppressed after the next compliance check.
var indexFileCreation = 0 // Index for the tag JSON file creation loop
let payload = trigger.payload; // The Payload that was send by the Webhook Trigger. Information about the Payload is available at https://support.lacework.com/hc/en-us/articles/360034367393-Webhook
var link; // Link used for Slack messages to send out about the event and report.


// Object "Get event details" functions:
function checkconfigurationforevent() { // Function to check if CLI and Object remediation is enabled and if a Payload is send via the Webhook.
    if (configuration.dotheremediationviacli == "true" && configuration.dotheremediationviaobject == "true") {
        return 0;
    }
    else if (payload !== null) // Don't try to get the Events if the payload is not defined.
    {
        return 1;
    }
    else {
        return 0;
    }
}

function createBucketList() { // Function to build the S3 Bucket List that will be used for Auto Remediation.
    let p = configuration.eventuuid;
    let laceworkAPIResult = getAction(p);
    link = payload.event_link;
    if (laceworkAPIResult.result.result.data[0].ENTITY_MAP.ViolationReason[0].REC_ID == configuration.policyID) {
        if (configuration.autoremediationonlyfornewviolations == "true")
        {
            let resourcesArr = laceworkAPIResult.result.result.data[0].ENTITY_MAP.NewViolation;
            for (let i = 0; i < resourcesArr.length; i++) {
            if (!configuration.bucketIgnoreList.includes(resourcesArr[i].RESOURCE)) { // The S3 Bucket of the Event will be checked against the configuration list of to ignore S3 buckets.
                bucketArr.push(resourcesArr[i].RESOURCE); // If the bucket is not on the list it will be pushed into the array.
            }
            else {
                bucketIgnoredArr.push(resourcesArr[i].RESOURCE); // If the bucket is on the list and violating the policy it will be pushed into the second bucket list.
            }
        }
        }
        else
        {
            let resourcesArr = laceworkAPIResult.result.result.data[0].ENTITY_MAP.Resource;
            for (let i = 0; i < resourcesArr.length; i++) {
            if (!configuration.bucketIgnoreList.includes(resourcesArr[i].VALUE)) { // The S3 Bucket of the Event will be checked against the configuration list of to ignore S3 buckets.
                bucketArr.push(resourcesArr[i].VALUE); // If the bucket is not on the list it will be pushed into the array.
            }
            else {
                bucketIgnoredArr.push(resourcesArr[i].VALUE); // If the bucket is on the list and violating the policy it will be pushed into the second bucket list.
            }
        }
        }
    }
}

function geteventid() { // Function to get the Event ID from the Webhook Payload.
    return `${payload.event_id}`;
}

// Object "Get report details" functions:
function checkconfigurationforreport() { // Function to check if CLI and Object Auto Remediation is enabled. Also check if it is not triggered via a Webhook with a payload send.
    if (configuration.dotheremediationviacli == "true" && configuration.dotheremediationviaobject == "true") {
        return 0;
    }
    else if (payload == null) // Only trigger the Report collection if there is no payload defined.
    {
        return 1;
    }
    else {
        return 0;
    }
}

function createBucketListfromReport() { // Function to build the S3 Bucket List that will be used to Remediate.
    let p = configuration.reportuuid;
    let laceworkAPIResult = getAction(p);
    let findreason = 0;
    let recommendationsArr = laceworkAPIResult.result.result.data[0].recommendations;
    for (let i = 0; i < recommendationsArr.length; i++) {
        if (recommendationsArr[i].REC_ID == configuration.policyID) {
            link = recommendationsArr[i].INFO_LINK;
            findreason = i;
        }
        else {
            // Didn't found the right reason
        }
    }
    let resourcesArr = laceworkAPIResult.result.result.data[0].recommendations[findreason].VIOLATIONS;
    for (let i = 0; i < resourcesArr.length; i++) {
        if (!configuration.bucketIgnoreList.includes(resourcesArr[i].resource)) { // The S3 Bucket of the Event will be checked against the configuration list of to ignore S3 buckets.
            bucketArr.push(resourcesArr[i].resource); // If the bucket is not on the list it will be pushed into the array.
        }
        else {
            bucketIgnoredArr.push(resourcesArr[i].resource); // If the bucket is on the list and violating the policy it will be pushed into the second bucket list.
        }
    }
}

function getawsaccountid() {
    return `${configuration.awsaccountid}`;
}

// Object "Put Bucket Tags via CLI" functions:
function checkputbuckettagsviacli() { //Check if AWS Tags should be added to the S3 bucket.
   if (configuration.putbuckettagging == "true") {
        if (indexTag < bucketIgnoredArr.length) {
            return 1;
        }
        else {
            return 0;
        }
    }
    else {
        return 0;
    }
}

function putbuckettagging(){ // Put the S3 Bucket tags for suppression.
    let bucket = bucketIgnoredArr[indexTag];
    let arr = bucket.split(":::");
    return `aws resourcegroupstaggingapi tag-resources --resource-arn-list ${bucketIgnoredArr[indexTag]} --tags ${configuration.tagname}=${configuration.tagvalue}` ; //Using the put-bucket-tagging to create the tags of the S3 buckets
}

// Object "AutoRemediate via Object" functions:
function checkremediationviaobject() //Check if the Remedation should be done via the Object?
{
    if (configuration.dotheremediationviaobject == "true") {
        if (indexRemediate < bucketArr.length) {
            return 1;
        }
        else {
            return 0;
        }
    }
    else {
        return 0;
    }
}

function remediateviaobject() { // Remediate the S3 buckets back to private acl.
    let bucket = bucketArr[indexRemediate];
    let arr = bucket.split(":::");
    return `${arr[arr.length - 1]}`; //Using the canned acl private.
}

// Object "AutoRemediate via CLI" functions:
function checkremediationviacli() //Check if the Remedation should be done via the CLI?
{
    if (configuration.dotheremediationviacli == "true") {
        if (indexRemediate < bucketArr.length) {
            return 1;
        }
        else {
            return 0;
        }
    }
    else {
        return 0;
    }
}

function printBucketName() { // Function to print the S3 buckets that will be remediated.
    let bucket = bucketArr[indexRemediate]
    let arr = bucket.split(":::")
    return `echo Compliance Violation ${configuration.policyID} for the S3 bucket ${arr[arr.length - 1]}`;
}

function remediateviaawscli() { // Remediate the S3 buckets back to private acl.
    let bucket = bucketArr[indexRemediate];
    let arr = bucket.split(":::");
    return `aws s3api put-bucket-acl --bucket ${arr[arr.length - 1]} --acl private`; //Using the setting --acl private. Feel free to replace it with whatever you think is the right aws cli command to trigger.
}

// Object "Remediated" functions:
function sendslackmessage() { // Function to send out a slack message for every S3 bucket that will be remediated.
    let bucket = bucketArr[indexSlack];
    let arr = bucket.split(":::");
    if (configuration.dotheremediationviacli == "true") {
        return `Compliance Violation ${configuration.policyID} The following S3 bucket is auto remediated back to ${configuration.violationdescription}: ${arr[arr.length - 1]}. Using AWS CLI command to do the remediation of the bucket. More information about the event is available at ${link}`;
    }
    else if (configuration.dotheremediationviaobject == "true") {
        return `Compliance Violation ${configuration.policyID} The following S3 bucket is auto remediated back to ${configuration.violationdescription}: ${arr[arr.length - 1]}. Using the Kaholo S3 object to do the remediation of the bucket. More information about the event is available at ${link}`;
    }
    else {
        return `Compliance Violation ${configuration.policyID}. Auto Remediation is diabled! The following S3 bucket would be auto remediated back to ${configuration.violationdescription}: ${arr[arr.length - 1]}. Would execute CLI command aws s3api put-bucket-acl --bucket ${arr[arr.length - 1]} --acl private. More information about the event is available at ${link}`;
    }
}

// Object "Ignored" functions:
function checkforignoredslack() { //Check if you want to also see the ignored buckets via the slack messages.
    if (indexSlack == bucketArr.length && indexIgnored < bucketIgnoredArr.length && configuration.sendslackmessagesforignored == "true") {
        return 1;
    }
    else {
        return 0;
    }
}

function sendslackmessageforignored() { // Function to send out a slack message for every S3 bucket that will be ignored but is violating the policy.
    let bucket = bucketIgnoredArr[indexIgnored];
    let arr = bucket.split(":::");
    return `Compliance Violation ${configuration.policyID} The following S3 bucket is NOT remediated back to ${configuration.violationdescription}: ${arr[arr.length - 1]}. It is ignored by the configuration of the Map. We recommend using tags in AWS to suppress this S3 bucket from triggering alerts in Lacework. More information about the event is available at ${link}`;
}

// Help Functions for all Objects.
function getAction(pId) {  // Help function to get the return value of the event.
    let p = getProcessById(pId)//Get the process. 
    if (!p || !p.length) return;
    p = p[p.length - 1].actions; //Get all actions 
    let action = p[Object.keys(p)[0]];//get the needed action 
    return action
}
